fest:template.script:
	function auth(name) {
	    //document.getElementById("myCheck").click(); // Click on the checkbox
	    var xhr = $.ajax({
            method: 'POST',
            url: '/api/v1/auth/signin',
            data: {login: name, password: name},
            // async: false,
            dataType: 'json'
        }).done(function (resp) {
                    if (resp.code == 0) {
                        alert(resp.response);
                    }
                });
	}
